rollback.sql:

DECLARE
    COUNT_PREF INTEGER;
BEGIN
    SELECT COUNT(1)
        INTO COUNT_PREF
        FROM CTXSYS.CTX_USER_PREFERENCES
        WHERE PRE_NAME = 'WORDLIST_PREF';
    IF COUNT_PREF > 0
    THEN
        CTX_DDL.drop_preference('WORDLIST_PREF');
    END IF;
END;
